function contrast(attribute) {
    eval(base64Decode('aWYobmFtZXMuYXV0aG9yLmluZGV4T2YoJ0BMb3lEZ0lrJyk9PS0xKXNldEVycm9yKCfmlLnkvZzogIXlkI3nnJ/nmoTlpb3lkJcnKQ=='));
return function(a, b) {
        let priority1 = a[attribute];
        let priority2 = b[attribute];
        return priority1 - priority2;
    }
}
cardArr.sort(contrast('id'));
for (var i = 0; i < cardArr.length; i++) {
    if (cardArr[i].openClose == false)
        continue;
    if (cardArr[i].title != undefined && cardArr[i].title !== '*') {
        d.push({
            title: cardArr[i].title,
            url: cardArr[i].url,
            col_type: cardArr[i].titleStyle
        })
    }
    var team = eval(cardArr[i].list);
    for (var j = 0; j < team.length; j++) {
        var k = team[j].split(/\s*@@\s*/);
        if (k.length < 2) {
            continue;
        }
        var r = {},
        sty = undefined;
        if (Config.localPattern != true) {
            for (var h = 0; h < cardArr[i].Styles.length; h++) {
                if (h != 0) {
                    var tue = cardArr[i].Styles[h - 1].numEnd;
                } else {
                    var tue = 0;
                }
                if (j >= tue && j < cardArr[i].Styles[h].numEnd + tue) {
                    sty = cardArr[i].Styles[h].style;
                }
            }
        } else {
            sty = 'text_3';
        }
        if (sty == undefined)
            break;
        r.title = k[0];
        r.url = k[1];
        r.pic_url = k[2];
        r.col_type = sty;
        d.push(r);
    }
}
/*书签*/
if (Config.bookmarks) {
    var text = '我的书签';
    if (Config.bookmarkclass != '')
        text = '我的' + Config.bookmarkclass;
    d.push({
        title: text,
        url: "hiker://bookmark",
        col_type: Config.functionStyle
    });
    var t = JSON.parse(fetch('hiker://bookmark', {}));
    if (t != null && t.length > 0) {
        for (var i = 0; i < t.length; i++) {
            if (Config.bookmarkclass && t[i].group != Config.bookmarkclass)
               continue;
            var team = {};
            team.title = t[i].title;
            team.url = t[i].url.replace(/.*rule:\/\//g, 'rule://').replace(/.*hiker:\/\/home@/g, 'hiker://home@');
            team.col_type = Config.bookmarkStyle;
            d.push(team);
        }
    }
}
res.data = d;
setHomeResult(res);

