js:
var res = {};
var items = [];
var html = getResCode();
eval(parseDom(html, 'body&&#SSR_HYDRATED_DATA&&Html').replace('window._SSR_HYDRATED_DATA', 'json'));
var json = json.complexSearch;
//setError(json)
for (var i = 0; i < json.data.length; i++) {
    var list = json.data[i];
    if (list.type == "lvideo") {
        if (list.data.display.hasOwnProperty('summary')) {
            list.data.display.length = 1;
        }
        for (var j = 0; j < list.data.display.length; j++) {
            data = list.data.display.length == 1 ? list.data.display : list.data.display[j];
            if (data.album_content[0].hasOwnProperty('album_subscript_name')) {
                items.push({
                    title: data.name,
                    desc: data.rating + ' · ' + data.album_content[0].album_subscript_name + ' · ' + data.album_content[0].last_seq,
                    img: data.video_cover_info.url,
                    content: data.summary,
                    url: 'https://www.ixigua.com/' + data.album_content[0].album_id
                });
            }
        }
    }
    break;
}
res.data = items;
setSearchResult(res);

