                            localRule.mappingTitle = localRule.mappingTitle != null && localRule.mappingTitle != "" ? localRule.mappingTitle : localRule.title.match(titleRegex)[0];
                            titleRegex = new RegExp(remoteRuleMappingTitle, "g");
